mapper.select:
SELECT count(*) FROM sale WHERE ur_state = 'Y'
AND addr_cd = #{addr_cd}
AND sal_i_cd LIKE CONCAT(#{sal_i_cd}, '%')


where true
and ur_state = 'Y'

    and seller_id = #{ur_id}


    and buyer_id = #{ur_id}


    and sal_s_cd = #{sal_s_cd}

and title like concat('%',#{keyword},'%')
ORDER BY no DESC
LIMIT #{offset},#{pageSize}

SELECT count(*)
FROM sale
where true
and ur_state = 'Y'

    and seller_id = #{ur_id}


    and buyer_id = #{ur_id}


    and sal_s_cd = #{sal_s_cd}

and title like concat('%',#{keyword},'%')

WHERE ur_state = 'Y'
AND addr_cd = #{addr_cd}
AND sal_i_cd LIKE CONCAT(#{sal_i_cd}, '%')
ORDER BY FIELD(sal_s_cd, 'S', 'R', 'C'), -- 판매중, 예약중, 거래완료 순으로 조회 h_date DESC, no DESC LIMIT #{pageSize} OFFSET #{offset}

mapper.update:
UPDATE sale
SET view_cnt = view_cnt + 1
WHERE no = #{no}

UPDATE sale
SET rv_state = 'N'
WHERE no = #{no}

mapper.select:
SELECT no, addr_name, sal_name, title, contents FROM sale WHERE ur_state = 'Y'
AND ( addr_name LIKE CONCAT('%', #{text}, '%') OR sal_name LIKE CONCAT('%', #{text}, '%') OR title LIKE CONCAT('%', #{text}, '%') OR contents LIKE CONCAT('%', #{text}, '%') )
ORDER BY h_date DESC, no DESC LIMIT #{pageSize} OFFSET #{offset}

WHERE ur_state = 'Y' -- 판매자ID AND seller_id = #{seller_id}
-- 판매글 제목 AND title LIKE CONCAT('%', #{title}, '%')
-- 판매글 상태(판매중/예약중/거래완료) AND sal_s_cd = #{sal_s_cd}
ORDER BY h_date DESC, no DESC
ORDER BY view_cnt DESC
and tx_s_cd = 'S' ORDER BY price ASC
and tx_s_cd = 'S' ORDER BY price DESC
LIMIT #{pageSize} OFFSET #{offset}

mapper.update:
update sale
set like_cnt = like_cnt+ #{cnt}
where no = #{no}
