mapper.select:
select count(*)
from review_comment
where sal_id = #{sal_id}


where sal_id = #{sal_id}
order by r_date desc, no desc
LIMIT #{offset},#{pageSize}

mapper.update:
update review_comment
set contents = #{contents}
  ,  reviewStar = #{reviewStar}
  , m_date = now()
  , last_date = now()
  , last_id = #{buy_id}
where buy_id = #{buy_id} and no = #{no}

update review_comment
    set ur_state = 'N'
where no = #{no}
